import './App.css';
import React, { useState, useRef, useEffect } from 'react';
import * as d3 from "d3";
import { useQuery } from 'react-query';




const fetchData = async () => {
  const response = await fetch('ws://localhost:8000/altitude');
  return response.json();
};


function App() {
  const [mockData] = useState([0,1,0,3,5,4,1,4,5]);
  const svgRef = useRef();

  useEffect(() => {
      // setting up svg
      const w = 500;
      const h = 200;
      const svg = d3.select(svgRef.current)
      .attr('width', w)
      .attr('height', h)
      .style('background', '#d3d3d3')
      .style('margin-top', '50')

      // setting the scaling

      // set the axes

      // setting up the data for svg

  }, [mockData])

  const { data, error, status } = useQuery('data', fetchData);

  if (status === 'loading') {
    return <div>Loading data...</div>;
  }

  if (status === 'error') {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className="App">
      <svg ref={svgRef}></svg> 

    <div>
      {data.map(item => (
        <div key={item.id}>{item.name}</div>
      ))}
    </div>

    </div>
  );
}

export default App;
